node.warn("Conversation output is:\n" + JSON.stringify(msg.payload))

if (msg.payload.output && msg.payload.output.text) {
    output = msg.payload.output.text.join(' ');
    if (output && output.indexOf("|tweet:") > -1) {
        node.warn("tweeting");
        if (
            msg.payload.context.twitter &&
            msg.payload.context.twitter.handle 
            ) {

            // extract text and url to tweet and clean up strings
            var tweet = output.split('|')
                .filter(function (c){
                    return c.indexOf("tweet:") > -1 ;
                }).join(', ').replace("tweet:", '');
            var url = output.split('|')
                .filter(function (c){
                    return c.indexOf("link:") > -1 ;
                }).join(', ').replace("link:", '');

            // dm switch
            var dmswitch = '';
            if (msg.payload.context.twitter.dm && msg.payload.context.twitter.dm === true){
                dmswitch = "DM ";
            }
            // construct tweet
            tweet = dmswitch + "@" + msg.payload.context.twitter.handle + " " + tweet + " " + url;

            msg.payload = tweet;
            node.warn("Sending string to twitter node: \n" + tweet)
            node.send([msg,null]);

        } else {node.error("no twitter details");}
    } else {
        node.warn("no tweet requested");
    }
    output = output.split('|')
    .filter(function(c) {
        return c.indexOf("tweet:") === -1 && c.indexOf("link:") === -1;
    }).join(' ');
} else {
    output = 'No response';
}

node.warn("Cleaned conversation output: \n" + output)
msg.payload = output;
